import React, { useState } from 'react';
import styled from 'styled-components';
import { InputGroup, FormControl, Button, Form, Col } from 'react-bootstrap';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { getRamal } from './RamalService';
 
const SearchHeader = styled.div`
    margin-top: 4px;
`;
const [search, setSearch] = useState('');

function SearchBar(props) {

    return (
        <SearchHeader>
            <Form onSubmit={handleOnSubmit(props)}>
                <Form.Row>
                    <Col className="col-xl-3">
                        <InputGroup className="mb-3">
                            <FormControl
                                placeholder="Search your ramal"
                                aria-label="Search your ramal"
                                onChange={handleSearchChange}
                            />
                            <InputGroup.Append>
                                <Button type="submit">
                                    <FontAwesomeIcon icon={faSearch} />
                                </Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </Col>
                </Form.Row>
            </Form>
        </SearchHeader>
    );
}
 
function handleOnSubmit(event) {
    event.preventDefault();
    const ramais = getRamal();
    const results = ramais.filter(ramais => ramais.nome.toLowerCase().indexOf(search) !== -1);
    event.setRamal(results);
}
 
function handleSearchChange(event) {
    setSearch(event.target.value.toLowerCase());
}
 
export default SearchBar;
