import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { InputGroup, FormControl, Button, Form, Col } from 'react-bootstrap';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { getRamal } from './RamalService';
 
const SearchHeader = styled.div`
    margin-top: 4px;
`;



function SearchBar(props) {

    const [search, setSearch] = useState('');
const history = useNavigate();
    
    return (
        <SearchHeader>
            <Form onSubmit={handleOnSubmit}>
                <Form.Row>
                    <Col className="col-xl-3">
                        <InputGroup className="mb-3">
                            <FormControl
                                placeholder="Search your books"
                                aria-label="Search your books"
                                onChange={handleSearchChange}
                            />
                            <InputGroup.Append>
                                <Button type="submit">
                                    <FontAwesomeIcon icon={faSearch} />
                                </Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </Col>
                    <Col className="col-xl-2">
                        <Form.Group controlId="order">
                            <Form.Control as="select">
                                <option>Newest</option>
                                <option>Oldest</option>
                            </Form.Control>
                        </Form.Group>
                    </Col>
                    <Col>
                        <Button variant="primary" className="float-right" onClick={newRamal}>
                            New Ramal
                            </Button>
                    </Col>
                </Form.Row>
            </Form>
        </SearchHeader>
    );
}
 
function newRamal() {
    history.push('/ramal/new');
}
 
function handleOnSubmit(event) {
    event.preventDefault();
    const ramais = getRamal();
    const results = ramais.filter(ramais => ramais.nome.toLowerCase().indexOf(search) !== -1);
    props.setRamal(results);
}
 
function handleSearchChange(event) {
    setSearch(event.target.value.toLowerCase());
}
 
export default SearchBar;
